import React, { useState, useEffect } from 'react';
import '../styles/Admin.css';
import trash from "../assets/trash.svg";
import axios from "axios";
import rightArrow from "../assets/rightArrow.svg";
import leftArrow from "../assets/leftArrow.svg";

const server_url = process.env.REACT_APP_SERVER_URL;

export default function Admin() {
  const [activeButton, setActiveButton] = useState(null);
  const [songs, setSongs] = useState([]);
  const [artists, setArtists] = useState([]);
  const [pageSongs, setPageSongs] = useState(1);
  const [pageArtists, setPageArtists] = useState(1);
  const [totalSongs, setTotalSongs] = useState(0);
  const [totalArtists, setTotalArtists] = useState(0);
  const [loading, setLoading] = useState(false);
  const [searchTermSongs, setSearchTermSongs] = useState("");
  const [searchTermArtists, setSearchTermArtists] = useState("");
  const limit = 10;

  const formatFollowers = (followers) => {
    return new Intl.NumberFormat().format(followers);
  };

  const formatDuration = (durationMs) => {
    const totalSeconds = Math.round(durationMs / 1000);
    const minutes = Math.floor(totalSeconds / 60);
    const seconds = totalSeconds % 60;
    return `${minutes}:${seconds.toString().padStart(2, '0')}`;
  };

  const formatGenres = (genres) => {
    if (!genres || genres.length === 0) {
      return "Sin géneros";
    }
    
    if (genres.length > 3) {
      return `${genres.slice(0, 3).join(", ")}...`;
    }

    return genres.join(", ");
  };

  const pressed = (button) => {
    setActiveButton(prev => (prev === button ? null : button));
  };

  const searchArtists = async (query) => {
    try {
      const response = await axios.get(`/api/artists/search/artists`, {
        params: { q: query },
      });
      return response.data;
    } catch (error) {
      console.error('Error al buscar artistas:', error);
      return [];
    }
  };

  const searchSongs = async (query) => {
    try {
      const response = await axios.get(`/api/songs/search/songs`, {
        params: { q: query },
      });
      return response.data;
    } catch (error) {
      console.error('Error al buscar canciones:', error);
      return [];
    }
  };

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      
      if (activeButton === "songs") {
        try {
          const songsResponse = await axios.get(`${server_url}/api/get/songs?page=${pageSongs}&limit=${limit}&q=${searchTermSongs}`);
          const totalSongsResponse = await axios.get(`${server_url}/api/get/songs/count`);
          setSongs(songsResponse.data);
          setTotalSongs(totalSongsResponse.data.total);
        } catch (error) {
          console.error('Error fetching songs data:', error);
        }
      }

      if (activeButton === "artists") {
        try {
          const artistsResponse = await axios.get(`${server_url}/api/get/artists?page=${pageArtists}&limit=${limit}&q=${searchTermArtists}`);
          const totalArtistsResponse = await axios.get(`${server_url}/api/get/artists/count`);
          setArtists(artistsResponse.data);
          setTotalArtists(totalArtistsResponse.data.total);
        } catch (error) {
          console.error('Error fetching artists data:', error);
        }
      }

      setLoading(false);
    };

    if (activeButton === "songs" || activeButton === "artists") {
      fetchData();
    }
  }, [activeButton, pageSongs, pageArtists, searchTermSongs, searchTermArtists]);

  const renderPagination = (currentPage, total, onPageChange) => {
    const totalPages = Math.ceil(total / limit);
    return (
      <div className="pagination">
        <button className='pagination-button' disabled={currentPage === 1} onClick={() => onPageChange(currentPage - 1)}><img src={leftArrow} alt="Previous" /></button>
        <span className='pagination-text'>Página {currentPage} de {totalPages}</span>
        <button className='pagination-button' disabled={currentPage === totalPages} onClick={() => onPageChange(currentPage + 1)}><img src={rightArrow} alt="Next" /></button>
      </div>
    );
  };

  return (
    <div className="admin-container">
      <aside className="sidebar">
        <button onClick={() => pressed("users")} className={`sidebar-btn ${activeButton === "users" ? "pressed" : ""}`}>Usuarios</button>
        <button onClick={() => pressed("songs")} className={`sidebar-btn ${activeButton === "songs" ? "pressed" : ""}`}>Canciones</button>
        <button onClick={() => pressed("artists")} className={`sidebar-btn ${activeButton === "artists" ? "pressed" : ""}`}>Artistas</button>
        <button onClick={() => pressed("graphs")} className={`sidebar-btn ${activeButton === "graphs" ? "pressed" : ""}`}>Gráficos</button>
      </aside>

      <main className="main-content">
        <h2 className="admin-header">Panel de admin</h2>

        {activeButton === "songs" && (
          <div className="admin-table">
            <input
              type="text"
              placeholder="Buscar canciones..."
              className="admin-input"
              value={searchTermSongs}
              onChange={(e) => setSearchTermSongs(e.target.value)}
            />
            {loading ? (
              <div className="d-flex justify-content-center align-items-center">
                <div className="spinner-border spinner-custom" role="status">
                  <span className="sr-only"></span>
                </div>
              </div>
            ) : (
              songs.map((song, i) => (
                <div className="admin-row" key={i}>
                  {song.spotify ? (
                    <>
                      <img src={song.spotify.album.images[0]?.url} alt="Cover" className='row-img'/>
                      <h3 className="row-name">{song.title}</h3>
                      <p className="row-info">Álbum: {song.spotify.album.name}</p>
                      <p className="row-info">Duración: {formatDuration(song.spotify.duration_ms)}</p>
                    </>
                  ) : (
                    <h3 className="row-name">{song.title}</h3>
                  )}
                  <button className="row-trash">
                    <img src={trash} alt="Eliminar" />
                  </button>
                </div>
              ))
            )}
            {renderPagination(pageSongs, totalSongs, setPageSongs)}
          </div>
        )}

        {activeButton === "artists" && (
          <div className="admin-table">
            <input
              type="text"
              placeholder="Buscar artistas..."
              className="admin-input"
              value={searchTermArtists}
              onChange={(e) => setSearchTermArtists(e.target.value)}
            />
            {loading ? (
              <div className="d-flex justify-content-center align-items-center">
                <div className="spinner-border spinner-custom" role="status">
                  <span className="sr-only"></span>
                </div>
              </div>
            ) : (
              artists.map((artist, i) => (
                <div className="admin-row" key={i}>
                  {artist.spotify ? (
                    <>
                      <img
                        src={artist.spotify.images[0]?.url || 'https://via.placeholder.com/50'}
                        alt={artist.name}
                        className='row-img'
                      />
                      <h3 className="row-name">{artist.spotify.name}</h3>
                      <p className="row-info">Géneros: {formatGenres(artist.spotify.genres)}</p>
                      <p className="row-info">Seguidores: {formatFollowers(artist.spotify.followers)}</p>
                    </>
                  ) : (
                    <h3 className="row-name">{artist.name}</h3>
                  )}
                  <button className="row-trash">
                    <img src={trash} alt="Eliminar" />
                  </button>
                </div>
              ))
            )}
            {renderPagination(pageArtists, totalArtists, setPageArtists)}
          </div>
        )}

        {activeButton === "graphs" && (
          <div className="admin-table">
            <p className='admin-text'>Mostrando gráficos...</p>
          </div>
        )}

        {!activeButton && (
          <div className="admin-table">
            <p className='admin-text'>Selecciona una opción...</p>
          </div>
        )}
      </main>
    </div>
  );
}
