import React, { useState, useEffect } from 'react';
import '../styles/Admin.css';
import trash from "../assets/trash.svg";
import axios from "axios";

const server_url = process.env.REACT_APP_SERVER_URL;

export default function Admin() {
  const [activeButton, setActiveButton] = useState(null);
  const [fetchedSongs, setFetchedSongs] = useState(false);
  const [songs, setSongs] = useState([]);
  const [artists, setArtists] = useState([]);
  const [pageSongs, setPageSongs] = useState(1);
  const [pageArtists, setPageArtists] = useState(1);
  const [totalSongs, setTotalSongs] = useState(0);
  const [totalArtists, setTotalArtists] = useState(0);
  const [loading, setLoading] = useState(false);
  const limit = 20;

  const pressed = (button) => {
    setActiveButton(prev => (prev === button ? null : button));
  };

  useEffect(() => {
    const fetchSpotifyData = async () => {
      setLoading(true); // Inicia el loading mientras enriquecemos las canciones con datos de Spotify

      const enrichedSongs = await Promise.all(songs.map(async (song) => {
        if (!song.spotify) { // Verificamos si la canciÃ³n ya tiene datos de Spotify
          try {
            const response = await axios.get(`${server_url}/get/songInfo?songName=${encodeURIComponent(song.title)}`, {
              withCredentials: true
            });
            const parsed = JSON.parse(response.data.tracks);
            const track = parsed.tracks.items[0];
            return { ...song, spotify: track };
          } catch (err) {
            return { ...song, spotify: null };
          }
        }
        return song; // Si ya tiene datos de Spotify, la retornamos tal cual
      }));
        setLoading(false); 
        setSongs(enrichedSongs);
    };

    if (activeButton === "songs" && songs.length > 0) {
        fetchSpotifyData();
    }
  }, [songs]);

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true); // Empieza la carga de canciones

      if (activeButton === "songs") {
        try {
          const songsResponse = await axios.get(`${server_url}/api/get/songs?page=${pageSongs}&limit=${limit}`);
          const totalSongsResponse = await axios.get(`${server_url}/api/get/songs/count`);
          setSongs(songsResponse.data);
          setTotalSongs(totalSongsResponse.data.total);
        } catch (error) {
          console.error('Error fetching songs data:', error);
        }
      }

      if (activeButton === "artists") {
        try {
          const artistsResponse = await axios.get(`${server_url}/api/get/artists?page=${pageArtists}&limit=${limit}`);
          const totalArtistsResponse = await axios.get(`${server_url}/api/get/artists/count`);
          setArtists(artistsResponse.data);
          setTotalArtists(totalArtistsResponse.data.total);
        } catch (error) {
          console.error('Error fetching artists data:', error);
        }
      }

        setLoading(false); // Finaliza la carga de canciones
    };

    if (activeButton) {
      fetchData();
    }
  }, [activeButton, pageSongs, pageArtists]);

  const renderPagination = (currentPage, total, onPageChange) => {
    const totalPages = Math.ceil(total / limit);
    return (
      <div className="pagination">
        <button disabled={currentPage === 1} onClick={() => onPageChange(currentPage - 1)}>Anterior</button>
        <span>PÃ¡gina {currentPage} de {totalPages}</span>
        <button disabled={currentPage === totalPages} onClick={() => onPageChange(currentPage + 1)}>Siguiente</button>
      </div>
    );
  };

  return (
    <div className="admin-container">
      <aside className="sidebar">
        <button
          onClick={() => pressed("users")}
          className={`sidebar-btn ${activeButton === "users" ? "pressed" : ""}`}
        >
          Usuarios
        </button>
        <button
          onClick={() => pressed("songs")}
          className={`sidebar-btn ${activeButton === "songs" ? "pressed" : ""}`}
        >
          Canciones
        </button>
        <button
          onClick={() => pressed("artists")}
          className={`sidebar-btn ${activeButton === "artists" ? "pressed" : ""}`}
        >
          Artistas
        </button>
        <button
          onClick={() => pressed("graphs")}
          className={`sidebar-btn ${activeButton === "graphs" ? "pressed" : ""}`}
        >
          GrÃ¡ficos
        </button>
      </aside>

      <main className="main-content">
        <h2 className="admin-header">Panel de admin</h2>

        {activeButton === "users" && (
          <div className="admin-table">
            <input type="text" placeholder="Buscar..." className="admin-input" />
            <div className="admin-row">
              <img src="https://via.placeholder.com/50" alt="Usuario" className="row-img" />
              <h3 className="row-name">Nombre del Usuario</h3>
              <p className="row-info">DescripciÃ³n breve o rol</p>
              <button className="row-trash">
                <img src={trash} alt="Eliminar" />
              </button>
            </div>
          </div>
        )}

        {activeButton === "songs" && (
          <div className="admin-table">
            {loading ? (
              <div className="d-flex justify-content-center align-items-center">
                <div className="spinner-border spinner-custom" role="status">
                  <span className="sr-only"></span>
                </div>
              </div>
            ) : (
              songs.map((song, i) => (
                <div className="admin-row" key={i}>
                  {song.spotify && (
                    <>
                      <img src={song.spotify.album.images[0]?.url} alt="Cover" width="50" />
                      <h3 className="row-name">{song.title}</h3>
                      <p className="row-info">Ãlbum: {song.spotify.album.name}</p>
                      <p className="row-info">DuraciÃ³n: {Math.round(song.spotify.duration_ms / 1000)}s</p>
                    </>
                  )}
                  <button className="row-trash">
                    <img src={trash} alt="Eliminar" />
                  </button>
                </div>
              ))
            )}
            {renderPagination(pageSongs, totalSongs, setPageSongs)}
          </div>
        )}

        {activeButton === "artists" && (
          <div className="admin-table">
            {loading ? (
              <div className="d-flex justify-content-center align-items-center">
                <div className="spinner-border spinner-custom" role="status">
                  <span className="sr-only"></span>
                </div>
              </div>
            ) : (
              artists.map((artist, i) => (
                <div className="admin-row" key={i}>
                  <h3 className="row-name">{artist.name}</h3>
                  <p className="row-info">{artist.id}</p>
                  <button className="row-trash">
                    <img src={trash} alt="Eliminar" />
                  </button>
                </div>
              ))
            )}
            {renderPagination(pageArtists, totalArtists, setPageArtists)}
          </div>
        )}

        {activeButton === "graphs" && (
          <div className="admin-table">
            <p className='admin-text'>Mostrando grÃ¡ficos...</p>
          </div>
        )}

        {!activeButton && (
          <div className="admin-table">
            <p className='admin-text'>Selecciona una opciÃ³n...</p>
          </div>
        )}
      </main>
    </div>
  );
}
