import React, { useState, useEffect } from 'react';
import '../styles/Admin.css';
import trash from "../assets/trash.svg";
import axios from "axios";

export default function Admin() {
  const [activeButton, setActiveButton] = useState(null);
  const [songs, setSongs] = useState([]);
  const [artists, setArtists] = useState([]);
  const [pageSongs, setPageSongs] = useState(1);
  const [pageArtists, setPageArtists] = useState(1);
  const [totalSongs, setTotalSongs] = useState(0);
  const [totalArtists, setTotalArtists] = useState(0);
  const limit = 20;

  const pressed = (button) => {
    setActiveButton(prev => (prev === button ? null : button));
  };

  useEffect(() => {
    if (activeButton === "songs") {
      axios.get(`/get/songs?page=${pageSongs}&limit=${limit}`).then(res => setSongs(res.data));
      axios.get("/get/songs/count").then(res => setTotalSongs(res.data.total));
    }
    if (activeButton === "artists") {
      axios.get(`/get/artists?page=${pageArtists}&limit=${limit}`).then(res => setArtists(res.data));
      axios.get("/get/artists/count").then(res => setTotalArtists(res.data.total));
    }
  }, [activeButton, pageSongs, pageArtists]);

  const renderPagination = (currentPage, total, onPageChange) => {
    const totalPages = Math.ceil(total / limit);
    return (
      <div className="pagination">
        <button disabled={currentPage === 1} onClick={() => onPageChange(currentPage - 1)}>Anterior</button>
        <span>Página {currentPage} de {totalPages}</span>
        <button disabled={currentPage === totalPages} onClick={() => onPageChange(currentPage + 1)}>Siguiente</button>
      </div>
    );
  };

  return (
    <div className="admin-container">
      <aside className="sidebar">
        <button
          onClick={() => pressed("users")}
          className={`sidebar-btn ${activeButton === "users" ? "pressed" : ""}`}
        >
          Usuarios
        </button>
        <button
          onClick={() => pressed("songs")}
          className={`sidebar-btn ${activeButton === "songs" ? "pressed" : ""}`}
        >
          Canciones
        </button>
        <button
          onClick={() => pressed("artists")}
          className={`sidebar-btn ${activeButton === "artists" ? "pressed" : ""}`}
        >
          Artistas
        </button>
        <button
          onClick={() => pressed("graphs")}
          className={`sidebar-btn ${activeButton === "graphs" ? "pressed" : ""}`}
        >
          Gráficos
        </button>
      </aside>

      <main className="main-content">
        <h2 className="admin-header">Panel de admin</h2>

        {activeButton === "users" && (
          <div className="admin-table">
            <input type="text" placeholder="Buscar..." className="admin-input" />
            <div className="admin-row">
              <img src="https://via.placeholder.com/50" alt="Usuario" className="row-img" />
              <h3 className="row-name">Nombre del Usuario</h3>
              <p className="row-info">Descripción breve o rol</p>
              <button className="row-trash">
                <img src={trash} alt="Eliminar" />
              </button>
            </div>
          </div>
        )}

        {activeButton === "songs" && (
          <div className="admin-table">
            {songs.map((song, i) => (
              <div className="admin-row" key={i}>
                <h3 className="row-name">{song.title}</h3>
                <p className="row-info">{song.id}</p>
                <button className="row-trash">
                  <img src={trash} alt="Eliminar" />
                </button>
              </div>
            ))}
            {renderPagination(pageSongs, totalSongs, setPageSongs)}
          </div>
        )}

        {activeButton === "artists" && (
          <div className="admin-table">
            {artists.map((artist, i) => (
              <div className="admin-row" key={i}>
                <h3 className="row-name">{artist.name}</h3>
                <p className="row-info">{artist.id}</p>
                <button className="row-trash">
                  <img src={trash} alt="Eliminar" />
                </button>
              </div>
            ))}
            {renderPagination(pageArtists, totalArtists, setPageArtists)}
          </div>
        )}

        {activeButton === "graphs" && (
          <div className="admin-table">
            <p className='admin-text'>Mostrando gráficos...</p>
          </div>
        )}

        {!activeButton && (
          <div className="admin-table">
            <p className='admin-text'>Selecciona una opción...</p>
          </div>
        )}
      </main>
    </div>
  );
}
